import React from 'react';
import { GoslingComponent } from 'gosling.js';

function SimpleExample() {
	return (
		<>
			<GoslingComponent
				spec={{ 
					tracks: [
						{
							alignment: 'overlay',
							title: 'Active enhancers',
							data: {
								url: './cell_type_enhancers/enahncers_13celltypes.csv',
								type: 'csv',
								chromosomeField: 'chr',
								genomicFields: ['p1', 'p2']
							},
							tracks: [
								{
									mark: 'rect',
									dataTransform: [
										{
											type: 'filter',
											field: 'gene',
											oneOf: ['astrocyte-ENCODE@'+'IL17RA'],
											not: true
										},
									],
									color: {
										field: 'gene',
										type: 'nominal',
										value: 'black'
									},
									size: {value: 60},
								},
								{
									mark: 'rect',
									dataTransform: [
										{
											type: 'filter', field: 'gene', oneOf: ['astrocyte-ENCODE@'+'IL17RA']
										}
									],
									size: {value: 60},
									color: {value: '#ff0000'},
									stroke:{value:'red'},
								},
							],
							tooltip: [
								{
									field: 'ind',
									type: 'quantitative',
									alt: 'Enhancer ID',
								},
								{field: 'p1', type: 'genomic', alt: 'Start Position'},
								{field: 'p2', type: 'genomic', alt: 'End Position'},
							],
							x: {field: 'p1', type: 'genomic'},
							xe: {field: 'p2', type: 'genomic'},
							color: {
								field: 'chr',
								type: 'nominal',
								range: ['black']
							},
							stroke: {
								field: 'chr',
								type: 'nominal',
								range: ['black']
							},
							strokeWidth: {value: 1},
							style: {
								background: '#F8F8F8',outline: 'black',legendTitle:'left'
							},
							width: 1383,
							height: 15
						},
					]
				}}
				experimental={{ reactive: true }}
			/>
		</>
	);
}

export default SimpleExample;
