import React, { useEffect, useRef, useState } from 'react';
import { GoslingComponent } from 'gosling.js';
import EnhancersURL from '../cell_type_enhancers/enahncers_13celltypes.csv'
import ArcsURL from '../cell_type_enhancers/astrocyte-ENCODE_arcs.csv'
function MouseEvents() {

	const gosRef = useRef();
	const [position, setPosition] = useState();
	const [data, setData] = useState([]);

	useEffect(() => {
		if(!gosRef.current) return;

		gosRef.current.api.subscribe('click', (_, eventData) => {
			const { genomicPosition: p } = eventData;
			const { data: d } = eventData;
			window.alert(d[0].gene);
			setPosition(`${p.chromosome}:${p.position}`)
			setData(eventData.data);
		});

		gosRef.current.api.subscribe('rangeSelect', (_, eventData) => {
			if(!eventData || !eventData.genomicRange) {
				// range selection cleared
				setPosition('N/A');
				setData([]);
			} else {
				const { genomicRange: p } = eventData;
				if(p.length === 2) setPosition(`${p[0].chromosome}:${p[0].position}-${p[1].chromosome}:${p[1].position}`);
				setData(eventData.data.length > 10 ? eventData.data.slice(0, 10) : eventData.data);
			}
		});

		return () => {
			gosRef.current.api.unsubscribe('click');
			gosRef.current.api.unsubscribe('rangeSelect');
		}
	}, [gosRef]);
	<div >
		hhh
	</div>

	return (
		<div>

			{position ? null: null}
			<GoslingComponent
				ref={gosRef}
				spec={{
					title: 'Results',
					style: {linkStyle: 'elliptical'},
					views: [
						{
							alignment: 'overlay',
							assembly: 'hg38',
							title: 'Gene annotation',
							linkingId: 'view1',
							xDomain: {chromosome: 'chr22', interval: [17084954-100000, 17115694+100000]},
							data: {
								url: 'https://higlass.io/api/v1/tileset_info/?d=P0PLbQMwTYGy-5uPIQid7A',
								type: 'beddb',
								genomicFields: [
									{index: 1, name: 'start'},
									{index: 2, name: 'end'}
								],
								valueFields: [
									{index: 5, name: 'strand', type: 'nominal'},
									{index: 3, name: 'name', type: 'nominal'}
								],
								exonIntervalFields: [
									{index: 12, name: 'start'},
									{index: 13, name: 'end'}
								]
							},
							tracks: [
								{
									experimental: { mouseEvents: true },
									dataTransform: [
										{type: 'filter', field: 'type', oneOf: ['gene']},
										{type: 'filter', field: 'strand', oneOf: ['+']}
									],
									mark: 'triangleRight',
									x: {field: 'end', type: 'genomic', axis: 'top'},
									size: {value: 15},
								},
								{
									experimental: { mouseEvents: true },
									dataTransform: [
										{type: 'filter', field: 'type', oneOf: ['gene']}
									],
									mark: 'text',
									text: {field: 'name', type: 'nominal'},
									x: {field: 'start', type: 'genomic'},
									xe: {field: 'end', type: 'genomic'},
									style: {dy: -15}
								},
								{
									experimental: { mouseEvents: true },
									dataTransform: [
										{type: 'filter', field: 'type', oneOf: ['gene']},
										{type: 'filter', field: 'strand', oneOf: ['-']}
									],
									mark: 'triangleLeft',
									x: {field: 'start', type: 'genomic'},
									size: {value: 15},
									style: {align: 'right'}
								},
								{
									experimental: { mouseEvents: true },
									dataTransform: [
										{type: 'filter', field: 'type', oneOf: ['exon']}
									],
									mark: 'rect',
									x: {field: 'start', type: 'genomic'},
									size: {value: 15},
									xe: {field: 'end', type: 'genomic'}
								},
								{
									experimental: { mouseEvents: true },
									dataTransform: [
										{type: 'filter', field: 'type', oneOf: ['gene']},
										{type: 'filter', field: 'strand', oneOf: ['+']}
									],
									mark: 'rule',
									x: {field: 'start', type: 'genomic'},
									strokeWidth: {value: 3},
									xe: {field: 'end', type: 'genomic'},
									style: {linePattern: {type: 'triangleRight', size: 5}}
								},
								{
									experimental: { mouseEvents: true },
									dataTransform: [
										{type: 'filter', field: 'type', oneOf: ['gene']},
										{type: 'filter', field: 'strand', oneOf: ['-']}
									],
									mark: 'rule',
									x: {field: 'start', type: 'genomic'},
									strokeWidth: {value: 3},
									xe: {field: 'end', type: 'genomic'},
									style: {linePattern: {type: 'triangleLeft', size: 5}}
								}
							],
							row: {field: 'strand', type: 'nominal', domain: ['+', '-']},
							color: {
								field: 'strand',
								type: 'nominal',
								domain: ['+', '-'],
								range: ['#7585FF', '#FF8A85']
							},
							visibility: [
								{
									operation: 'less-than',
									measure: 'width',
									threshold: '|xe-x|',
									transitionPadding: 10,
									target: 'mark'
								}
							],
							tooltip: [
								{
									field: 'name',
									type: 'quantitative',
									alt: 'Gene(exon)',
								},
								{
									field: 'strand',
									type: 'quantitative',
									alt: 'Strand',
								},
								{field: 'start', type: 'genomic', alt: 'Start Position'},
								{field: 'end', type: 'genomic', alt: 'End Position'},
							],
							opacity: {value: 0.8},
							width: screen.width*0.95,
							height: 100
						},


						// Cell type specific enhancer_track
						{
							alignment:':overlay',
							assembly: 'hg38',
							linkingId: 'view1',
							xDomain: {chromosome: 'chr22', interval: [17084954-100000, 17115694+100000]},
							tracks: [
								{
									alignment: 'overlay',
									title: 'Active enhancers',
									experimental: { mouseEvents: true },
									data: {
										url: EnhancersURL,
										type: 'csv',
										chromosomeField: 'chr',
										genomicFields: ['p1', 'p2']
									},
									tracks: [
										{
											mark: 'rect',
											dataTransform: [
												{
													type: 'filter',
													field: 'gene',
													oneOf: ['astrocyte-ENCODE@'+'IL17RA'],
													not: true
												},
											],
											color: {
												field: 'gene',
												type: 'nominal',
												value: 'black'
											},
											size: {value: 60},
										},
										{
											mark: 'rect',
											dataTransform: [
												{
													type: 'filter', field: 'gene', oneOf: ['astrocyte-ENCODE@'+'IL17RA']
												}
											],
											size: {value: 60},
											color: {value: '#ff0000'},
											stroke:{value:'red'},
										},
									],
									tooltip: [
										{
											field: 'ind',
											type: 'quantitative',
											alt: 'Enhancer ID',
										},
										{field: 'p1', type: 'genomic', alt: 'Start Position'},
										{field: 'p2', type: 'genomic', alt: 'End Position'},
									],
									x: {field: 'p1', type: 'genomic'},
									xe: {field: 'p2', type: 'genomic'},
									color: {
										field: 'chr',
										type: 'nominal',
										range: ['black']
									},
									stroke: {
										field: 'chr',
										type: 'nominal',
										range: ['black']
									},
									strokeWidth: {value: 1},
									style: {
										background: '#F8F8F8',outline: 'black',legendTitle:'left'
									},
									width: screen.width*0.95,
									height: 15
								},

								{
									alignment: 'overlay',
									title:'Astrocyte-ENCODE',
									id: 'heatmap-track',
									data: {
										url: ArcsURL,
										type: 'csv',
										chromosomeField: 'chr',
										genomicFields: ['center', 'TSS']
									},
									dataTransform: [
										{type: 'filter', field: 'gene', oneOf: ['IL17RA']},
									],
									tracks: [
										{mark: 'withinLink'},
									],
									tooltip: [
										{
											field: 'gene',
											type: 'quantitative',
											alt: 'Connected  Gene',
										},
										{
											field: 'score',
											type: 'quantitative',
											alt: 'ABC score (0~1)',
											format: '.4'
										},
										{field: 'TSS', type: 'genomic', alt: 'Gene TSS'},
										{field: 'center', type: 'genomic', alt: 'Enhancer Region Center'},
									],
									x: {field: 'TSS', type: 'genomic', linkingId: 'view1'},
									xe: {field: 'center', type: 'genomic'},
									color: { field: 'score', type: 'quantitative', legend: true, range: 'pink' },
									stroke: {value: 'red'},
									strokeWidth: {value: 1},
									opacity: {value: 1},
									style: {background: '#F8F8F8',outline: 'black',legendTitle:'left'},
									width: screen.width*0.95,
									height: 60
								}
							]
						},

					]
				}}
				experimental={{ reactive: true }}
			/>
			{data.length === 0 ? null : null}
		</div>
	);
}

export default MouseEvents;
